import React, { useState } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import HomePage from './HomePage';
import AddBookPage from './AddBookPage';
import EditBookPage from './EditBookPage';

const App = () => {
  const [books, setBooks] = useState([
    { id: '1', title: 'Book One', author: 'Author One', description: 'Description One' },
    { id: '2', title: 'Book Two', author: 'Author Two', description: 'Description Two' },
    { id: '3', title: 'Book Three', author: 'Author Three', description: 'Description Three' },
  ]);

  const addBook = (book) => {
    setBooks([...books, book]);
  };

  const editBook = (updatedBook) => {
    setBooks(books.map((book) => (book.id === updatedBook.id ? updatedBook : book)));
  };

  const deleteBook = (id) => {
    setBooks(books.filter((book) => book.id !== id));
  };

  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Container>
        <Navbar bg="dark" data-bs-theme="dark">
        <Container>
        <Navbar.Brand as={Link} to="/">Book List</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link as={Link} to="/">Home</Nav.Link>
              <Nav.Link as={Link} to="/add">Add Book</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>

      </Navbar>


        </Container>
      </Navbar>
      <Container className="mt-4">
        <Routes>
          <Route path="/" element={<HomePage books={books} deleteBook={deleteBook} />} />
          <Route path="/add" element={<AddBookPage addBook={addBook} />} />
          <Route path="/edit/:id" element={<EditBookPage books={books} editBook={editBook} />} />
        </Routes>
      </Container>
    </div>
  );
};

export default App;
