import React, { useState } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import HomePage from './HomePage';
import AddBookPage from './AddBookPage';
import EditBookPage from './EditBookPage';

const App = () => {
  const [books, setBooks] = useState([]);

  const addBook = (book) => {
    setBooks([...books, book]);
  };

  const editBook = (updatedBook) => {
    setBooks(books.map((book) => (book.id === updatedBook.id ? updatedBook : book)));
  };

  const deleteBook = (id) => {
    setBooks(books.filter((book) => book.id !== id));
  };

  return (
    <div className="container">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <Link className="navbar-brand" to="/">Book List</Link>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/add">Add Book</Link>
            </li>
          </ul>
        </div>
      </nav>
      <Routes>
        <Route path="/" element={<HomePage books={books} deleteBook={deleteBook} />} />
        <Route path="/add" element={<AddBookPage addBook={addBook} />} />
        <Route path="/edit/:id" element={<EditBookPage books={books} editBook={editBook} />} />
      </Routes>
    </div>
  );
};

export default App;
