import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Button, Row, Col } from 'react-bootstrap';

const HomePage = ({ books, deleteBook }) => {
  return (
    <div>
      <h1 className="my-4">Book List</h1>
      <Row>
        {books.map((book) => (
          <Col md={4} key={book.id} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{book.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">Author: {book.author}</Card.Subtitle>
                <Card.Text>{book.description}</Card.Text>
                <Link to={`/edit/${book.id}`} className="btn btn-primary mr-2">Edit</Link>
                <Button variant="danger" onClick={() => deleteBook(book.id)}>Delete</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default HomePage;
